var navOpenButton=document.querySelector(".nav__button--open"),navCloseButton=document.querySelector(".nav__button--close"),nav=document.querySelector(".nav");if(navOpenButton){var fadeEffect=function(e){e.classList.contains("fade_in")?e.classList.remove("fade_in"):setTimeout(function(){e.classList.add("fade_in")},600)};navOpenButton.addEventListener("click",function(){nav.classList.add("nav_open");var e=document.querySelector(".nav__item");fadeEffect(e)}),navCloseButton.addEventListener("click",function(){var e=document.querySelector(".nav__item");fadeEffect(e),setTimeout(function(){nav.classList.remove("nav_open")},600)})}