var navOpenButton=document.querySelector(".nav__button--open"),navCloseButton=document.querySelector(".nav__button--close"),nav=document.querySelector(".nav"),navOverlay=document.querySelector(".nav__overlay");if(navOpenButton){var fadeEffect=function(e){e.forEach(function(e){e.classList.contains("fade_in")?e.classList.remove("fade_in"):setTimeout(function(){e.classList.add("fade_in")},600)})};navOpenButton.addEventListener("click",function(){nav.classList.add("nav_open"),navOverlay.classList.add("nav__overlay--open");var e=document.querySelectorAll(".nav__item");fadeEffect(e)}),navCloseButton.addEventListener("click",function(){var e=document.querySelectorAll(".nav__item");fadeEffect(e),setTimeout(function(){nav.classList.remove("nav_open"),navOverlay.classList.remove("nav__overlay--open")},600)}),navOverlay.addEventListener("click",function(){var e=document.querySelectorAll(".nav__item");fadeEffect(e),setTimeout(function(){nav.classList.remove("nav_open"),navOverlay.classList.remove("nav__overlay--open")},600)})}console.log("testing video player js");